#!/bin/bash

CONVERT=./convert

echo -e "\n ----------Invalid Input----------\n"
echo "./convert invalidinput"
$CONVERT invalidinput
echo "./convert 15f"
$CONVERT 15f
echo "./convert 0000m"
$CONVERT 0000m
echo "./convert '   '"
$CONVERT '   '
echo "./convert 42.42ff"
$CONVERT 42.42ff

echo -e "\n ----------Char----------\n"

echo -e "./convert 'a'"
$CONVERT 'a'
echo -e "\n./convert 'c'"
$CONVERT 'c'
echo -e "\n./convert '*'"
$CONVERT '*'

echo -e "\n ----------Int----------\n"

echo -e "\n./convert 42"
$CONVERT 42
echo -e "\n./convert 0"
$CONVERT 0
echo -e "\n./convert -42"
$CONVERT -42
echo -e "\n./convert INT_MAX"
$CONVERT 2147483647
echo -e "\n./convert INT_MIN"
$CONVERT -2147483648
echo -e "\n./convert INT_OVERFLOW"
$CONVERT 2147483648
echo -e "\n./convert INT_UNDERFLOW"
$CONVERT -2147483649

echo -e "\n ----------Float----------\n"

echo -e "./convert 0.0f"
$CONVERT 0.0f
echo -e "\n./convert 4.2f"
$CONVERT 4.2f
echo -e "\n./convert -4.2f"
$CONVERT -4.2f
echo -e "\n./convert nanf"
$CONVERT nanf
echo -e "\n./convert +inff"
$CONVERT +inff
echo -e "\n./convert -inff"
$CONVERT -inff

echo -e "\n./convert FLT_MAX"
$CONVERT 340282346638528859811704183484516925440.0f
echo -e "\n./convert FLT_MIN"
$CONVERT -340282346638528859811704183484516925440.0000000000000000f
echo -e "\n./convert FLT_OVERFLOW"
$CONVERT 340282446638528859811704183484516925455.9f
echo -e "\n./convert FLT_UNDERFLOW"
$CONVERT -340282446638528859811704183484516925499.9900000000000000f


echo -e "\n ----------Double----------\n"

echo -e "./convert 0.0"
$CONVERT 0.0
echo -e "\n./convert 4.2"
$CONVERT 4.2
echo -e "\n./convert -4.2"
$CONVERT -4.2
echo -e "\n./convert nan"
$CONVERT nan
echo -e "\n./convert +inf"
$CONVERT +inf
echo -e "\n./convert -inf"
$CONVERT -inf

echo -e "\n./convert DBL_MAX"
$CONVERT .0
echo -e "\n./convert DBL_MIN"
$CONVERT -.0
echo -e "\n./convert DBL_OVERFLOW"
$CONVERT .9
echo -e "\n./convert DBL_UNDERFLOW"
$CONVERT -.9
